import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import axios from "axios";

const BowlingStats = () => {
  const [data, setData] = useState([]);

  // Function to fetch the Excel file and process it
  const fetchData = async () => {
    try {
      // Fetch the file from the public folder
      const response = await axios.get("/bowling_weekly_scores.xlsx", {
        responseType: "arraybuffer",
      });

      // Read the Excel file
      const workbook = XLSX.read(response.data, { type: "array" });

      // Assuming your data is in the first sheet
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];

      // Convert sheet to JSON
      const jsonData = XLSX.utils.sheet_to_json(sheet);
      setData(jsonData);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  // Fetch data on component mount and set up polling every 60 seconds
  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 60000); // Poll every 60 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  return (
    <div>
      <h1>Bowling Stats</h1>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Bowler</th>
            <th>Historical Average</th>
            <th>High Score</th>
            <th>Wins</th>
            <th>Highest Weekly Average (1st)</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 &&
            data.map((row, index) => (
              <tr key={index}>
                <td>{row.Bowler}</td>
                <td>{row["Historical Average"]}</td>
                <td>{row["High Score"]}</td>
                <td>{row.Wins}</td>
                <td>{row["Highest Weekly Average (1st)"]}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default BowlingStats;
